const doodle = document.querySelector('css-doodle');

/* update styles and refresh */
doodle.update(`
    :doodle {
        @grid: 8 / 90%;
    }

    transition: .2s @r(.6s);
    border-radius: @pick(100% 0, 0 100%);

    will-change: transform;
    transform: scale(@r(.25, 1.25));

    background: hsla(
        calc(240 - 6 * @x * @y),
        70%, 68%, @r.8
    );
`);

/* just refresh */
doodle.update();

// export default {doodle.update};
