import React, { useState } from "react";
import LineTitle from "../Components/LineTitle"


function Login() {
  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);

  const database = [
    {
      username: "user1",
      password: "pass1"
    },
    {
      username: "user2",
      password: "pass2"
    }
  ];

  const errors = {
    uname: "invalid username",
    pass: "invalid password"
  };

  const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    var { uname, pass } = document.forms[0];  //inputs from form

    // Find username in db
    const userData = database.find((user) => user.username === uname.value);

    // Compare password
    if (userData) {
      if (userData.password !== pass.value) {
        // Invalid password
        setErrorMessages({ name: "pass", message: errors.pass });
      } else {
        //valid password and username
        setIsSubmitted(true);
      }
    } else {
      // Username not found
      setErrorMessages({ name: "uname", message: errors.uname });
    }
  };

  // Error message
  const renderErrorMessage = (name) => {
    name === errorMessages.name && (
      <div className="SmFont CursiveFont Error">{errorMessages.message}</div>
    );
  }

  // Login form
  const renderForm = (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <div className="">
          <label>Username </label>
          <input type="text" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div className="">
          <label>Password </label>
          <input type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div className="button-container">
          <input type="submit" />
        </div>
      </form>
    </div>
  );

  return (
    <div className="container ">
      <div className="outer ToCenter DarkGreenBox LoginBox BlogBox CurvedContainer">
      <div className="middle">
        <LineTitle Light={true} Title="Login in" OnlyLine={false}/>
        {isSubmitted ? <div className="MdFont CursiveFont LightGreenFontShade">User successfully logged in</div> : renderForm}
      </div></div>
    </div>
  );
}

export default Login;
