import React, { useState } from "react";
import LineTitle from "../Components/LineTitle"
import { Form, Button } from "react-bootstrap";


function Login() {
  const [errorMessages, setErrorMessages] = useState("blh");
  const [isSubmitted, setIsSubmitted] = useState(false);

  const database = [
    {
      username: "user1",
      password: "pass1"
    },
    {
      username: "user2",
      password: "pass2"
    }
  ];

  const errors = {
    uname: "Invalid Username",
    pass: "Invalid Password"
  };

  const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    var { uname, pass } = document.forms[0];  //inputs from form

    // Find username in db
    const userData = database.find((user) => user.username === uname.value);
    // console.log(uname.value);
    // console.log(pass.value);

    // Compare password
    if (userData) {
      if (userData.password !== pass.value) {
        // Invalid password
        setErrorMessages({ name: "pass", message: errors.pass });
      } else {
        //valid password and username
        setIsSubmitted(true);
      }
    } else {
      // Username not found
      setErrorMessages({ name: "uname", message: errors.uname });
      // console.log(errorMessages.message);
    }
  };

  // Error message
  const renderErrorMessage = (name) => {
    if (name === errorMessages.name ) {
      // console.log("i work fxn           - " + errorMessages.message)
      return (
        <div className="Error VySmFont CursiveFont">{errorMessages.message}</div>
      );
    }
  }

  // Login form
  const renderForm = (
    <Form className="container" onSubmit={handleSubmit} style={{color:"#48466D"}}>

        <Form.Group className="mb-3" controlId="formBasicName">
          <Form.Label className="InputLabel">Username</Form.Label>
          <Form.Control className="InputFocusGreen" type="text" name="uname" required />

        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label className="InputLabel">Password</Form.Label>
          <Form.Control className="InputFocusGreen" type="password" name="pass" required />

        </Form.Group>

        {renderErrorMessage("uname")}{renderErrorMessage("pass")}

        <Button className="ButtonStyle" variant="primary" type="submit">Submit</Button>

      </Form>
  );

  return (
    <div className="container ">
      <div className="outer ToCenter DarkGreenBox LoginBox BlogBox CurvedContainer">
      <div className="middle">
        <LineTitle Light={true} Title="Login" OnlyLine={false}/>
        {isSubmitted ? <div className="MdFont CursiveFont LightGreenFontShade">User successfully logged in</div> : renderForm}
        <LineTitle Light={true} OnlyLine={true}/>
      </div></div>
    </div>
  );
}

export default Login;
